#include "Course.h"

Course::Course(string name, Teacher* teacher, Department* department, int capacity) {
    this->name=name;
    this->teacher=teacher;
    this->department=department;
    this->capacity=capacity;
}

bool Course::addGrade(Student* student, float grade) {
    grades[student].push_back(grade);
    return true;
}

bool Course::addStudent(Student* student) {
    if(this->currentCapacity<this->capacity)
    {
        for(int i=0;i<students.size();++i)
        {
            if(students[i]==student)
            {
                return false;
            }
        }
        currentCapacity++;
        students.push_back(student);
        return true;
    }
    else
    return false;
}

void Course::setMaterials(string file) {
    this->materials=file;
}

string Course::getMaterials() {
    return this->materials;
}

map<Student*,vector<float>> Course::getGrades() {
    return this->grades;
}

string Course::getName() {
    return this->name;
}

Teacher* Course::getTeacher() {
    return this->teacher;
}

vector<Student*> Course::getStudents() {
    return this->students;
}

Department* Course::getDepartment() {
    return this->department;
}

